function u(e=new Date().toISOString()){return e.split("T")[0]}function p(e=new Date().toISOString()){let t=new Date(e),n=t.getFullYear(),r=String(t.getMonth()+1).padStart(2,"0"),a=String(t.getDate()).padStart(2,"0"),o=String(t.getHours()).padStart(2,"0"),i=String(t.getMinutes()).padStart(2,"0");return`${n}-${r}-${a}T${o}:${i}`}function s(e,t={}){return new Intl.DateTimeFormat("en-NG",t).format(e)}export{u as a,p as b,s as c};
